mapper.update:
update tbl_activity set name=#{name},owner=#{owner},startDate=#{startDate},
                        endDate=#{endDate},cost=#{cost},description=#{description},
                        editTime=#{editTime},editBy=#{editBy}
where id = #{id}

mapper.select:
select count(*)
from tbl_activity a join tbl_user u on a.owner=u.id


        and a.name like '%' #{name} '%'


        and u.name like '%' #{owner} '%'


        and startDate >= #{startDate}


        and endDate <= #{endDate}

select a.id as id, a.name as name, u.name as owner, startDate, endDate from tbl_activity a join tbl_user u on a.owner=u.id
and a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and startDate >= #{startDate}
and endDate <= #{endDate}
order by a.startDate desc,a.name limit #{offset},#{pageSize}
